mapper.select:
SELECT NVL(SUM(PASS_COUNT) , 0) PASS_COUNT
FROM PASS
WHERE USER_NO = #{userNo}

mapper.update:
UPDATE PASS
SET PASS_COUNT = PASS_COUNT - #{deductPass}
WHERE USER_NO = #{userNo}

UPDATE PASSRECORD
SET PP_COUNT = PP_COUNT - #{deductPass}
WHERE USER_NO = #{userNo}

UPDATE PASSRECORD
SET PP_STATUS = 1,
FACILITY_NO = #{facilityNo},
USE_CODE = #{useCode}
WHERE USER_NO = #{userNo}

mapper.select:
SELECT COUNT(*)
FROM PASSRECORD
WHERE USER_NO = #{userNo}
AND FACILITY_NO = #{facilityNo}
AND PP_STATUS = 0

mapper.update:
