mapper.update:
UPDATE BOARD
SET STATUS = 'N'
WHERE BOARD_NO = #{boardNo}

mapper.select:
SELECT BOARD_NO, BOARD_TITLE, BOARD_CONTENT, FAQ_CATEGORY
FROM BOARD B
WHERE BOARD_NO = #{boardNo}

mapper.update:
UPDATE BOARD
SET BOARD_TITLE = #{boardTitle}, 
	BOARD_CONTENT = #{boardContent},
	MODIFY_DATE = SYSDATE,
	FAQ_CATEGORY = #{faqCategory}
WHERE BOARD_NO = #{boardNo}

